mapper.select:
      SELECT CAST(v.id AS CHAR) id,CAST(v.user_id AS CHAR) userId,u.UserName userName,v.gold_condition goldCondition,v.deduction_value deductionValue,
	  v.deduction_percent deductionPercent,v.description,v.create_time createTime,v.use_status useStatus,v.use_time useTime,
	  v.date_start dateStart,v.date_end dateEnd,v.type,v.source,
      CAST(i.agent_id as CHAR) AS agentId, ua.user_name AS agentName, CAST(i.broker_id as CHAR) AS brokerId, ub.user_name AS brokerName 
      FROM user_voucher v
      LEFT JOIN User_Login u ON v.user_id = u.UserID
      LEFT JOIN User_Info i ON u.UserID = i.UserID
      LEFT JOIN users ua ON i.agent_id = ua.id
      LEFT JOIN users ub ON i.broker_id = ub.id

          and u.Status = 1

            and u.UserName = #{userName,jdbcType=VARCHAR}


              and v.create_time >=#{startTime,jdbcType=TIMESTAMP}


              and v.create_time <=#{endTime,jdbcType=TIMESTAMP}


              and v.use_time >=#{useStartTime,jdbcType=TIMESTAMP}


              and v.use_time <=#{useEndTime,jdbcType=TIMESTAMP}


              and ua.id = #{agentName,jdbcType=BIGINT}


              and ub.id = #{brokerName,jdbcType=BIGINT}


              and v.use_status = #{useStatus,jdbcType=INTEGER}


        order by v.create_time DESC
