SELECT o.id id, o.order_no orderNo, o.user_id userID, u.UserName userName, i.RegisterTime  registerTime,
i.agent_id AS agentId, ua.user_name AS agentName, i.broker_id AS brokerId, ub.user_name AS brokerName ,
o.product_id productId, c.product_name productName,c.product_no productNo,c.cycle,c.year_incom_percent yearIncomPercent,
c.float_percent floatPercent, c.nper nper, c.type type,
o.buy_amount buyAmount,o.buy_time buyTime,o.redeem_time redeemTime,o.status,o.income,
o.initial_price initialPrice, o.clear_price clearPrice, 
s.share_amount shareAmount 
FROM finance_order o
LEFT JOIN User_Login u ON o.user_id = u.UserID
LEFT JOIN User_Info i ON u.UserID = i.UserID
LEFT JOIN users ua ON i.agent_id = ua.id
LEFT JOIN users ub ON i.broker_id = ub.id
LEFT JOIN finance_conf c ON o.product_id = c.id
LEFT JOIN share_record s on o.id = s.order_id

  and u.Status = 1 and o.type = 1

    and u.UserName = #{userName,jdbcType=VARCHAR}


    and o.order_no = #{orderNo,jdbcType=VARCHAR}


    and o.buy_time >=#{startTime,jdbcType=VARCHAR}


    and o.buy_time <=#{endTime,jdbcType=TIMESTAMP}


    and i.RegisterTime >=#{regStartTime,jdbcType=TIMESTAMP}


    and i.RegisterTime <=#{regEndTime,jdbcType=TIMESTAMP}


    and o.redeem_time >=#{redeemStartTime,jdbcType=TIMESTAMP}


    and o.redeem_time <=#{redeemEndTime,jdbcType=TIMESTAMP}


    and ua.id = #{agentName,jdbcType=BIGINT}


    and ub.id = #{brokerName,jdbcType=BIGINT}


    and o.status = #{status,jdbcType=INTEGER}


    and c.nper = #{nper,jdbcType=VARCHAR}


    and c.type = #{type,jdbcType=INTEGER}


order by o.buy_time DESC

