SELECT DATE_FORMAT(fo.buy_time,'%y-%m-%d') randomTime, COUNT(fo.id) AS randomCount, SUM(fo.buy_amount) AS randomAmount FROM finance_order fo LEFT JOIN finance_conf fc ON fo.product_id=fc.id LEFT JOIN User_Info ui ON fo.user_id=ui.UserID LEFT JOIN users us ON ui.agent_id = us.id
fc.type=1
AND fo.buy_time >=#{startTime,jdbcType=TIMESTAMP}
AND fo.buy_time <=#{endTime,jdbcType=TIMESTAMP}
AND us.user_name =#{agentName,jdbcType=VARCHAR}
GROUP BY DATE_FORMAT(fo.buy_time,'%m-%d-%Y') ORDER BY DATE_FORMAT(fo.buy_time,'%m-%d-%Y') DESC

DROP TABLE IF EXISTS tmp_table; CREATE TEMPORARY TABLE tmp_table(`time` datetime DEFAULT NULL);
insert into tmp_table (time) values (#{dt,jdbcType=TIMESTAMP})
; SELECT t.time time,t1.realGoldAmount,t1.realGoldCount,t2.goldRightAmount,t2.goldRightCount,t3.financeAmount,t3.financeCount,t4.goldUpAmount,t4.goldUpCount FROM tmp_table t LEFT JOIN (SELECT DATE_FORMAT(rgo.buy_time,'%y-%m-%d') AS realGoldTime, COUNT(rgo.id) AS realGoldCount, SUM(rmb_amount) AS realGoldAmount FROM real_gold_order rgo GROUP BY DATE_FORMAT(rgo.buy_time,'%m-%d-%Y') ORDER BY DATE_FORMAT(rgo.buy_time,'%m-%d-%Y') DESC) t1 on t.time=t1.realGoldTime left join (SELECT DATE_FORMAT(dor.create_time,'%y-%m-%d') AS goldRightTime, COUNT(dor.id) AS goldRightCount, SUM(dor.ensure_amount) AS goldRightAmount FROM deal_order dor GROUP BY DATE_FORMAT(dor.create_time,'%m-%d-%Y') ORDER BY DATE_FORMAT(dor.create_time,'%m-%d-%Y') DESC) t2 on t.time = t2.goldRightTime left join (SELECT DATE_FORMAT(fof.buy_time,'%y-%m-%d') AS financeTime, COUNT(fof.id) AS financeCount, SUM(fof.buy_amount) AS financeAmount FROM finance_order fof where fof.type = 1 GROUP BY DATE_FORMAT(fof.buy_time,'%m-%d-%Y') ORDER BY DATE_FORMAT(fof.buy_time,'%m-%d-%Y') DESC) t3 on t.time = t3.financeTime left join (SELECT DATE_FORMAT(fog.buy_time,'%y-%m-%d') AS goldUpTime, COUNT(fog.id) AS goldUpCount, SUM(fog.buy_amount) AS goldUpAmount FROM finance_order fog where fog.type = 2 GROUP BY DATE_FORMAT(fog.buy_time,'%m-%d-%Y') ORDER BY DATE_FORMAT(fog.buy_time,'%m-%d-%Y') DESC) t4 on t.time = t4.goldUpTime
realGoldAmount is not null or goldRightAmount is not null or financeAmount is not null or goldUpAmount is not null
ORDER BY time desc limit #{start}, #{size}
