mapper.select:
select
o.order_id orderId, o.product_id productId, o.product_no productNo, o.product_name
productName,
o.product_type productType, concat(convert(o.product_year_incom_percent*100,DECIMAL(6,2)),'%') productYearIncomPercent,
o.product_cycle productCycle, o.product_min_gram productMinGram, o.product_max_gram
productMaxGram,
o.product_settle_method productSettleMethod, o.buy_type buyType,o.buy_amount buyAmount,
o.buy_time buyTime,
o.expire_time expireTime, o.status, o.income_gram incomeGram, o.buy_price buyPrice,
o.back_total_gram backTotalGram,o.voucher_value voucherValue, o.voucher_deductible
voucherDeductible,o.gram,

    INSERT(u.UserName, 4, 4, '****') userName,


    u.UserName userName,


(select user_name from users where id = o.agent_id) agentName,
(select user_name from users where id = o.broker_id) brokerName,
u1.RegisterTime registerTime
from finance_regulargold_order o
left join User_Login u
on o.user_id = u.UserID
left join User_Info u1
on u.UserId = u1.UserId


        AND o.buy_time >= #{startTime,jdbcType=TIMESTAMP}


        AND o.buy_time <= #{endTime,jdbcType=TIMESTAMP}


        AND o.expire_time >= #{redeemStartTime,jdbcType=TIMESTAMP}


        AND o.expire_time <= #{redeemEndTime,jdbcType=TIMESTAMP}


        AND o.status = #{status,jdbcType=SMALLINT}


        AND o.buy_time = #{buyType,jdbcType=SMALLINT}


        AND o.agent_id = #{agentName,jdbcType=BIGINT}


        AND o.broker_id = #{brokerName,jdbcType=BIGINT}



        AND u1.RegisterTime >= #{regStartTime,jdbcType=TIMESTAMP}


        AND u1.RegisterTime <= #{regEndTime,jdbcType=TIMESTAMP}




        AND u.UserName like CONCAT(#{userName,jdbcType=VARCHAR},'%')

select o.order_id orderId, o.product_id productId, o.product_no productNo, o.product_name productName, IF(o.product_type = 1,'定期金','特价金') productType, concat(convert(o.product_year_incom_percent*100,DECIMAL(6,2)),'%') productYearIncomPercent, o.product_cycle productCycle, o.product_min_gram productMinGram, o.product_max_gram productMaxGram, o.product_settle_method productSettleMethod, IF(o.buy_type=1,'现金买入','实金转入') buyType,o.buy_amount buyAmount, o.buy_time buyTime, o.expire_time expireTime, IF(o.status = 1,'持有中','已赎回') status, o.income_gram incomeGram, o.buy_price buyPrice, o.back_total_gram backTotalGram,o.voucher_value voucherValue, o.voucher_deductible voucherDeductible,o.gram,
INSERT(u.UserName, 4, 4, '****') userName,
u.UserName userName,
(select user_name from users where id = o.agent_id) agentName, (select user_name from users where id = o.broker_id) brokerName, u1.RegisterTime registerTime from finance_regulargold_order o left join User_Login u on o.user_id = u.UserID left join User_Info u1 on u.UserId = u1.UserId
AND o.buy_time >= #{startTime,jdbcType=TIMESTAMP}
AND o.buy_time <= #{endTime,jdbcType=TIMESTAMP}
AND o.expire_time >= #{redeemStartTime,jdbcType=TIMESTAMP}
AND o.expire_time <= #{redeemEndTime,jdbcType=TIMESTAMP}
AND o.status = #{status,jdbcType=SMALLINT}
AND o.buy_time = #{buyType,jdbcType=SMALLINT}
AND o.agent_id = #{agentName,jdbcType=BIGINT}
AND o.broker_id = #{brokerName,jdbcType=BIGINT}
AND u1.RegisterTime >= #{regStartTime,jdbcType=TIMESTAMP}
AND u1.RegisterTime <= #{regEndTime,jdbcType=TIMESTAMP}
AND u.UserName like CONCAT(#{userName,jdbcType=VARCHAR},'%')

select sum(buy_amount) totalAmount
from finance_regulargold_order o
left join User_Login u
on o.user_id = u.UserID
left join User_Info u1
on u.UserId = u1.UserId


        AND o.buy_time >= #{startTime,jdbcType=TIMESTAMP}


        AND o.buy_time <= #{endTime,jdbcType=TIMESTAMP}


        AND o.expire_time >= #{redeemStartTime,jdbcType=TIMESTAMP}


        AND o.expire_time <= #{redeemEndTime,jdbcType=TIMESTAMP}


        AND o.status = #{status,jdbcType=SMALLINT}


        AND o.buy_time = #{buyType,jdbcType=SMALLINT}


        AND o.agent_id = #{agentName,jdbcType=BIGINT}


        AND o.broker_id = #{brokerName,jdbcType=BIGINT}



        AND u1.RegisterTime >= #{regStartTime,jdbcType=TIMESTAMP}


        AND u1.RegisterTime <= #{regEndTime,jdbcType=TIMESTAMP}




        AND u.UserName like CONCAT(#{userName,jdbcType=VARCHAR},'%')
