mapper.select:
SELECT a.user_id userId,
l.UserName userName,
INSERT(l.UserName, 4, 4, '****') userName,
i.RealName realName,i.RegisterTime registerTime, i.agent_id AS agentId, ua.user_name AS agentName, i.broker_id AS brokerId, ub.user_name AS brokerName, a.rmb, ga.total_income totalIncome, ifnull(r.rj, 0) rj,ifnull(c.cj, 0) cj,ifnull(r.rj, 0)-ifnull(c.cj, 0) jrj, ifnull(tj.tj, 0) tj, ifnull(lc.lc, 0) lc, ifnull(hjb.hjb, 0) hjb,ifnull(hjs.hjs, 0) hjs FROM User_Login l LEFT JOIN user_account a ON a.user_id = l.UserID LEFT JOIN user_gold_account ga ON ga.user_id = l.UserID LEFT JOIN User_Info i ON l.UserID = i.UserID LEFT JOIN users ua ON i.agent_id = ua.id LEFT JOIN users ub ON i.broker_id = ub.id LEFT JOIN (SELECT UserID, SUM(RMBAmt) rj FROM User_Recharge WHERE STATUS=1 GROUP BY UserID) r ON r.UserID = a.user_id LEFT JOIN (SELECT UserID, SUM(WithdrawAmt) cj FROM User_WithdrawCash WHERE Status = 1 GROUP BY UserID) c ON c.UserID = a.user_id LEFT JOIN (SELECT user_id, sum(gold_total_weight) tj FROM invest_gold_order WHERE status in ('10','20','30') GROUP BY user_id) tj ON tj.user_id = a.user_id LEFT JOIN (SELECT user_id, format(sum(gram), 3) lc from finance_regulargold_order where status = 1 and buy_type = 1 GROUP BY user_id) lc on lc.user_id = a.user_id LEFT JOIN (SELECT user_id, format(sum(amount), 3) hjb FROM save_gold_record WHERE type = 1 GROUP BY user_id) hjb ON hjb.user_id = a.user_id LEFT JOIN (SELECT user_id, format(sum(amount), 3) hjs FROM save_gold_record WHERE type = 4 GROUP BY user_id) hjs ON hjs.user_id = a.user_id
and (r.rj != 0 || c.cj != 0)
and l.UserName = #{userName,jdbcType=VARCHAR}
and ua.id = #{agentName,jdbcType=VARCHAR}
and ub.id = #{brokerName,jdbcType=VARCHAR}
order by i.RegisterTime desc

		SELECT RechargeChannel rechargeChannel, unix_timestamp(DATE_FORMAT(RechargeTime,'%Y-%c-%d')) rechargeTime, SUM(RMBAmt) rmbAmt
		FROM User_Recharge 

		AND STATUS=1


              and DATE_FORMAT(RechargeTime,'%Y-%c-%d') >=#{startTime,jdbcType=TIMESTAMP}


              and DATE_FORMAT(RechargeTime,'%Y-%c-%d') <=#{endTime,jdbcType=TIMESTAMP}


				and RechargeChannel = #{channel,jdbcType=VARCHAR}


		GROUP BY RechargeChannel,DATE_FORMAT(RechargeTime,'%Y-%m-%d')

SELECT a.agentName, SUM(a.rjSum) rjSum, SUM(a.cjSum) cjSum FROM ( SELECT u.user_name agentName, ( SELECT SUM(RMBAmt) rj FROM User_Recharge r WHERE r. STATUS = 1 AND r.agent_id = u.id
and DATE_FORMAT(RechargeTime,'%Y-%m-%d') >=#{startTime,jdbcType=TIMESTAMP}
and DATE_FORMAT(RechargeTime,'%Y-%m-%d') <=#{endTime,jdbcType=TIMESTAMP}
) rjSum, ( SELECT SUM(WithdrawAmt) cj FROM User_WithdrawCash c WHERE c. STATUS = 1 AND c.agent_id = u.id
and DATE_FORMAT(WithdrawTime,'%Y-%m-%d') >=#{startTime,jdbcType=TIMESTAMP}
and DATE_FORMAT(WithdrawTime,'%Y-%m-%d') <=#{endTime,jdbcType=TIMESTAMP}
) cjSum FROM users u WHERE u.pid = 1
) a WHERE a.rjSum IS NOT NULL OR a.cjSum IS NOT NULL GROUP BY a.agentName

SELECT a.agentName, IFNULL(SUM(a.rjSum),0)-IFNULL(SUM(a.cjSum),0) jrjSum FROM ( SELECT u.user_name agentName, ( SELECT SUM(RMBAmt) rj FROM User_Recharge r WHERE r. STATUS = 1 AND r.agent_id = u.id
and DATE_FORMAT(RechargeTime,'%Y-%m-%d') >=#{startTime,jdbcType=TIMESTAMP}
and DATE_FORMAT(RechargeTime,'%Y-%m-%d') <=#{endTime,jdbcType=TIMESTAMP}
) rjSum, ( SELECT SUM(WithdrawAmt) cj FROM User_WithdrawCash c WHERE c. STATUS = 1 AND c.agent_id = u.id
and DATE_FORMAT(WithdrawTime,'%Y-%m-%d') >=#{startTime,jdbcType=TIMESTAMP}
and DATE_FORMAT(WithdrawTime,'%Y-%m-%d') <=#{endTime,jdbcType=TIMESTAMP}
) cjSum FROM users u WHERE u.pid = 1
) a WHERE a.rjSum IS NOT NULL OR a.cjSum IS NOT NULL GROUP BY a.agentName
