select
u.UserName userName,
INSERT(u.UserName, 4, 4, '****') userName,
cast(o.id as CHAR) id, cast(o.user_id as CHAR) userId, o.invest_gold_id investGoldId, o.invest_gold_name investGoldName, o.invest_gold_weight investGoldWeight, o.invest_gold_service investGoldService, o.logistics_fee logisticsFee, o.logistics_no logisticsNo, o.gold_num goldNum, o.gold_total_weight goldTotalWeight, o.pay_type payType, o.gold_base_price goldBasePrice, o.processing_service processingService, o.invoice_service invoiceService,o.total_money totalMoney, o.gold_money goldMoney, o.service_money serviceMoney, o.img_url imgUrl, o.status, o.create_time createTime, o.send_time sendTime, o.update_time updateTime, DATE_FORMAT(o.pay_time,'%Y-%m-%d %H:%i:%s') payTime, o.agent_id AS agentId, ua.user_name AS agentName, o.broker_id AS brokerId, ub.user_name AS brokerName, a.contact_name contactName, a.contact_phone contactPhone, a.delivery_address deliveryAddress from invest_gold_order o LEFT JOIN User_Login u ON o.user_id = u.UserID LEFT JOIN user_address a on o.user_address_id = a.id LEFT JOIN users ua ON o.agent_id = ua.id LEFT JOIN users ub ON o.broker_id = ub.id
o.status != 0 and o.pay_type between 1 and 2
and u.UserName = #{userName,jdbcType=VARCHAR}
and o.create_time >=#{startTime,jdbcType=TIMESTAMP}
and o.create_time <=#{endTime,jdbcType=TIMESTAMP}
and o.status = #{status,jdbcType=INTEGER}
and o.pay_type = #{payType,jdbcType=INTEGER}
and ua.id = #{agentName,jdbcType=BIGINT}
and ub.id = #{brokerName,jdbcType=BIGINT}
order by o.create_time DESC

select
u.UserName userName,
INSERT(u.UserName, 4, 4, '****') userName,
cast(o.id as CHAR) id, cast(o.user_id as CHAR) userId, o.invest_gold_id investGoldId, o.invest_gold_name investGoldName, o.invest_gold_weight investGoldWeight, o.invest_gold_service investGoldService, o.logistics_fee logisticsFee, o.logistics_no logisticsNo, o.gold_num goldNum, o.gold_total_weight goldTotalWeight, o.pay_type payType, o.gold_base_price goldBasePrice, o.processing_service processingService, (select invoice_service from invest_gold_order where id= o.id and invoice = 1) invoiceService, o.total_money totalMoney, (select (gold_money*0.05+gold_total_weight*5) from invest_gold_order where id = o.id and pay_type = 3 ) discountAmount, DATE_FORMAT((select update_time from invest_gold_order where id= o.id and status BETWEEN 30 and 50),'%Y-%m-%d %H:%i:%s') endTime, (select service_money from invest_gold_order where id = o.id and pay_type BETWEEN 3 and 5 ) actualPayment, o.gold_money goldMoney, o.service_money serviceMoney, o.img_url imgUrl, o.status, o.create_time createTime, o.send_time sendTime, o.update_time updateTime, DATE_FORMAT(o.pay_time,'%Y-%m-%d %H:%i:%s') payTime, o.agent_id AS agentId, ua.user_name AS agentName, o.broker_id AS brokerId, ub.user_name AS brokerName, o.deliver_name contactName, o.deliver_phone contactPhone, o.user_address deliveryAddress from invest_gold_order o LEFT JOIN User_Login u ON o.user_id = u.UserID LEFT JOIN user_address a on o.user_address_id = a.id LEFT JOIN users ua ON o.agent_id = ua.id LEFT JOIN users ub ON o.broker_id = ub.id
o.status in (0,10,20,30,100) and o.pay_type between 3 and 5
and u.UserName = #{userName,jdbcType=VARCHAR}
and o.create_time >=#{startTime,jdbcType=TIMESTAMP}
and o.create_time <=#{endTime,jdbcType=TIMESTAMP}
and o.status = #{status,jdbcType=INTEGER}
and o.pay_type = #{payType,jdbcType=INTEGER}
and ua.id = #{agentName,jdbcType=BIGINT}
and ub.id = #{brokerName,jdbcType=BIGINT}
order by o.create_time DESC

    select 
    	sum(o.logistics_fee) logisticsFeeSum, sum(o.service_money) serviceMoneySum,sum(o.invest_gold_service) investGoldServiceSum, sum(o.gold_total_weight) goldNumSum
	from invest_gold_order o
	LEFT JOIN User_Login u ON o.user_id = u.UserID
    LEFT JOIN users ua ON o.agent_id = ua.id 
    LEFT JOIN users ub ON o.broker_id = ub.id

    	 o.status in (10,20,30) and o.pay_type between 1 and 2

	    	and u.UserName = #{userName,jdbcType=VARCHAR}


	    	and o.create_time >=#{startTime,jdbcType=TIMESTAMP}


	    	and o.create_time <=#{endTime,jdbcType=TIMESTAMP}


