mapper.select:
SELECT o.id id, o.order_no orderNo, o.user_id userID, u.UserName userName,DATE_FORMAT(i.RegisterTime, '%Y-%m-%d %h:%i:%s')  registerTime,
i.agent_id AS agentId, ua.user_name AS agentName, i.broker_id AS brokerId, ub.user_name AS brokerName ,
o.gold_product_id goldProductId, c.name productName,o.buy_price buyPrice, o.gram gram,
o.rmb_amount rmbAmount, o.fee fee,DATE_FORMAT(o.buy_time, '%Y-%m-%d %h:%i:%s') buyTime
FROM real_gold_order o
LEFT JOIN User_Login u ON o.user_id = u.UserID
LEFT JOIN User_Info i ON u.UserID = i.UserID
LEFT JOIN users ua ON i.agent_id = ua.id
LEFT JOIN users ub ON i.broker_id = ub.id
LEFT JOIN real_gold_conf c ON o.gold_product_id = c.id

    and u.Status = 1

      and i.userName = #{userName,jdbcType=VARCHAR}


        and o.order_no = #{orderNo,jdbcType=VARCHAR}


        and o.buy_time >=#{startTime,jdbcType=TIMESTAMP}


        and o.buy_time <=#{endTime,jdbcType=TIMESTAMP}


        and i.RegisterTime >=#{regStartTime,jdbcType=TIMESTAMP}


        and i.RegisterTime <=#{regEndTime,jdbcType=TIMESTAMP}


        and ua.id = #{agentName,jdbcType=VARCHAR}


        and ub.id = #{brokerName,jdbcType=VARCHAR}


  order by o.buy_time DESC

  SELECT SUM(gram) gramSum, SUM(fee) feeSum
  FROM real_gold_order o      
  LEFT JOIN User_Login u ON o.user_id = u.UserID
	LEFT JOIN User_Info i ON u.UserID = i.UserID
	LEFT JOIN users ua ON i.agent_id = ua.id
	LEFT JOIN users ub ON i.broker_id = ub.id
	LEFT JOIN real_gold_conf c ON o.gold_product_id = c.id

      and u.Status = 1

      and i.userName = #{userName,jdbcType=VARCHAR}


        and o.order_no = #{orderNo,jdbcType=VARCHAR}


        and o.buy_time >=#{startTime,jdbcType=TIMESTAMP}


        and o.buy_time <=#{endTime,jdbcType=TIMESTAMP}


        and i.RegisterTime >=#{regStartTime,jdbcType=TIMESTAMP}


        and i.RegisterTime <=#{regEndTime,jdbcType=TIMESTAMP}


        and ua.id = #{agentName,jdbcType=VARCHAR}


        and ub.id = #{brokerName,jdbcType=VARCHAR}

SELECT DATE_FORMAT(rgo.buy_time,'%y-%m-%d') AS realGoldTime, COUNT(rgo.id) AS realGoldCount, SUM(rmb_amount) AS realGoldAmount FROM real_gold_order rgo LEFT JOIN User_Info ui ON rgo.user_id=ui.UserID LEFT JOIN users us ON ui.agent_id=us.id
AND rgo.buy_time >=#{startTime,jdbcType=TIMESTAMP}
AND rgo.buy_time <=#{endTime,jdbcType=TIMESTAMP}
AND us.user_name =#{agentName,jdbcType=VARCHAR}
GROUP BY DATE_FORMAT(rgo.buy_time,'%m-%d-%Y') ORDER BY DATE_FORMAT(rgo.buy_time,'%m-%d-%Y') DESC
